class CommentClass{constructor(e){console.log("calling comment ajax ",e),this.postContainer=$("#post-"+e),this.commentForm=$(`#post-${e}-comment-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){console.log("deleting comments"),t.deleteComment($(this))}))}createComment(e){let t=this;this.commentForm.submit((function(o){o.preventDefault();$.ajax({type:"post",url:"/comments/newcomment",data:$(this).serialize(),success:function(o){let n=t.newCommentDom(o.data.comment);console.log($(`#post-${e}-comment-list>ul`)),$(`#post-${e}-comment-list>ul`).prepend(n),t.deleteComment($(".delete-comment-button",n)),new ToggleLike($(".toggle-like-button",n)),new Noty({theme:"metroui",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`<li id="comment-${e._id}">\n        <p>\n            ${e.user.name}\n            <br>\n            <small>\n                ${e.content}\n            </small>\n            <br>\n        <small>\n                <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Comment">\n                    <i class="fas fa-thumbs-up"></i> 0Likes\n                </a>\n        </small>\n        &nbsp;\n            <small>\n                <a class="delete-comment-button" href="/comments/destroy/${e._id}">Delete</a>\n            </small>\n    \n        </p>\n    </li>\n    `)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$("#comment-"+e.data.comment_id).remove(),new Noty({theme:"metroui",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log("error.responseText")}})}))}}